mapper.select:
SELECT title
FROM project
WHERE pno = #{pno}

mapper.insert:
INSERT INTO SCHEDULE
values(seq_sno.nextval, #{pno}, #{sname}, #{status}, #{progress}, 
	to_date(#{startDate_s}, 'YYYY-MM-DD'), to_date(#{endDate_s}, 'YYYY-MM-DD'), #{budget});

mapper.update:
UPDATE SCHEDULE 
SET progress = #{progress},
WHERE sno = #{sno}
